mapper.select:
select a.* from advancement a
where a.advancementNo =
#{advancementNo}

SELECT COUNT(*) FROM advancement
title LIKE CONCAT('%', #{searchWord}, '%')
writer LIKE CONCAT('%', #{searchWord}, '%')
content LIKE CONCAT('%', #{searchWord}, '%')
rejectMessage LIKE CONCAT('%', #{searchWord}, '%')
AND status IN ('PASS', 'FAILURE')
AND status IN ('WAITING', 'CHECKED')
AND status = #{status}
AND postDate >= #{searchStartDate}
AND postDate <= #{searchEndDate}

mapper.update:
UPDATE advancement
status = 'PASS' , confirmDate = NOW() , rejectMessage = null
WHERE advancementNo = #{advancementNo}

UPDATE advancement

    status = 'FAILURE'
    , confirmDate = NOW()
    , rejectMessage = #{rejectMessage}

WHERE advancementNo = #{advancementNo}

mapper.select:
SELECT refUser
FROM advancement
WHERE advancementNo = #{advancementNo}
