mapper.update:
UPDATE users
SET requiresVerification = 'N'
WHERE


		email = #{value}


		mobile = #{value}

mapper.select:
select * from `users` where userId = #{userId}
and password
= sha1(md5(#{password}))

SELECT EXISTS (
SELECT 1 FROM users WHERE userId = #{userId}
)

mapper.update:
UPDATE users
SET requiresVerification = 'Y'
WHERE userId = #{userId}
